    setIsAscending(!isAscending);
  }

if (!isAscending) {
    moves.reverse();
}

export default function ToggleSortOrder() {
    return(
        <button onClick={sortOrder}>
        {isAscending ? 'Trier par ordre décroissant' : 'Trier par ordre croissant'}
        </button>
    )
}
