export function setDescription(move, currentMove, col, row) {
    switch (move) {
        case currentMove:
            return ('Tour actuel : ' + move);
        case 0:
          return ('Revenir au début');
        default:
          return (`Aller au coup n°${move} (colonne : ${col}, ligne : ${row})`);
      }
}
