import { useState } from 'react';
import Board from './board.js';
import { calculateWinner } from './board.js';
import { Move } from './move.js';
import { handlePlay } from './handlePlay.js';


export default function Game() {
  const [history, setHistory] = useState([{ squares: Array(9).fill(null), location: 0 }]);
  const [currentMove, setCurrentMove] = useState(0);
  const [isAscending, setIsAscending] = useState(true);
  const xIsNext = currentMove % 2 === 0;
  const currentSquares = history[currentMove].squares;

  function jumpTo(nextMove) {
    setCurrentMove(nextMove);
  }

  function toggleSortOrder() {
    setIsAscending(!isAscending);
  }

  if (!isAscending) {
    moves.reverse();
  }

  const winnerInfo = calculateWinner(currentSquares);
  const winningSquares = winnerInfo ? winnerInfo.line : null;

  return (

    <div className="game">
      <div className="game-board">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} winningSquares={winningSquares} />
      </div>
      <div className="game-info">
        <button onClick={toggleSortOrder}>
          {isAscending ? 'Trier par ordre décroissant' : 'Trier par ordre croissant'}
        </button>
        <ol>{history.map(({location}, historyIndex) => (
          <Move jumpTo={jumpTo} location={location} historyIndex={historyIndex} currentMove={currentMove} />
        ))
        }</ol>
      </div>
    </div>
  );
}
