function getDescription(move, currentMove, col, row) {
    switch (move) {
        case currentMove:
            return ('Tour actuel : ' + move);
        case 0:
          return ('Revenir au début');
        default:
          return (`Aller au coup n°${move} (colonne : ${col}, ligne : ${row})`);
      }
}



export default function Moves({ history, currentMove }) {
  const moves = history.map((squares, move) => {
    const location = squares.location;
    const col = location % 3 + 1;
    const row = Math.floor(location / 3) + 1;
    let description = getDescription(move, currentMove, col, row);

    return (
      <li key={move}>
        {move === currentMove ? ( <p>{description}</p>) : (<button onClick={() => jumpTo(move)}>{description}</button>)}
      </li>
    );
  });

  return (
    <ol>{moves}</ol>
  );
}

function jumpTo(nextMove) {
  setCurrentMove(nextMove);
}

function toggleSortOrder() {
  setIsAscending(!isAscending);
}

if (!isAscending) {
  moves.reverse();
}
