import { useDescription } from "./useDescription";

export const Move = ({ historyEntry, jumpTo, currentMove}) => {
    const { squares, location } = historyEntry;
    const description = useDescription(squares, location, currentMove);
    return (
        <li key={location}>
          {location === currentMove ? ( <p>{description}</p>) : (<button onClick={() => jumpTo(location)}>{description}</button>)}
        </li>
      );
}
