export function setDescription(move, currentMove, col, row, description) {
    switch (move) {
        case currentMove:
            return (description = 'Tour actuel : ' + move);
        case 0:
          return (description = 'Revenir au début');
        default:
          return (description = `Aller au coup n°${move} (colonne : ${col}, ligne : ${row})`);
      }
}
